const debug = /\b(y|all|livereload)\b/.test (process.env.DEBUG) && console.warn // eslint-disable-line
const axios = require('axios')
const ws = require('ws')
const express = require('express')
const { URL } = require('url')

module.exports = class LiveReload {
  async start(port) {
    if (!parseInt(port))  port = 35729
    this.files = []

    // install the JS script for the client
    const app = express()
    app.use('/livereload.js', express.static(require.resolve('livereload-js')))

    return new Promise ((resolve, reject) => {
      let server
      const listening = async () => {
        const {address,port} = server.address()
        startWs(server)
        this.url = await livereloadURL(address, port)
        debug && debug(`[cds] - live reload available at ${this.url}`)
        resolve(this.url)
      }
        // start server
      server = app.listen (port,'localhost')
      server.once('listening', listening)
      server.on('error', e => {
        // try once more w/ ephemeral port 0
        if (e.code === 'EADDRINUSE') {
          server = app.listen (0,'localhost')
          server.once('listening', listening)
          server.on('error', reject)
        }
        else reject(e)
      })

      // start websocket
      const startWs = (server) => {
        this.wsServer = new ws.Server({ server })
        this.wsServer.on('connection', (socket) => {
          debug && debug('Client connected')
          socket.on('message', (msg) => {
            debug && debug('Client message', msg)
            const request = JSON.parse(msg)

            if (request.command === 'hello') {
              debug && debug('Client handshake')
              const data = JSON.stringify({
                command: 'hello',
                protocols: [
                  'http://livereload.com/protocols/official-7',
                  'http://livereload.com/protocols/official-8',
                  'http://livereload.com/protocols/official-9',
                  'http://livereload.com/protocols/2.x-origin-version-negotiation',
                  'http://livereload.com/protocols/2.x-remote-control',
                ],
                serverName: 'cds-watch',
              })
              socket.send(data)
            } else if (request.command === 'info') {
              debug && debug('Server received client data. Not sending response.')
            }
          })
        })
        this.wsServer.on('close', () => { debug && debug('Socket closed') })
        this.wsServer.on('error', (e) => debug && debug(e))
      }
      const close = () => { this.wsServer.close(); server.close(); debug && debug('Reload server closed') }
      process.once('SIGTERM', close)
      process.once('SIGINT', close)
    })
  }

  markPending(files=[]) {
    this.files = files
  }

  reload() {
    const data = JSON.stringify({
      command: 'reload',
      path: this.files.length ? this.files[0] : '',
    })
    debug && debug(`[cds] - live reload for ${this.files}. ${this.wsServer.clients.size} ws clients`)
    broadcast(data, this.wsServer.clients)
    this.files = []
  }

  get address() {
    return this.url ? new URL(this.url).host : null
  }
}

function broadcast(data, sockets) {
  sockets.forEach(socket => {
    debug && debug(`Sending ${data}`)
    socket.send(data, err=> debug && debug(err))
  })
}

async function livereloadURL(address, port) {
  if (process.env.WS_BASE_URL) {
    // on BAS, expose the livereload port as an externally accessible URL
    try {
      const resp = await axios.get('http://localhost:3001/AppStudio/api/getHostByPort?port=' + port)
      return resp.data.result + 'livereload.js?snipver=1&port=443'
    } catch (e) {
      console.error(`Error exposing live reload port ${port}: ${e.message}`)
      return
    }
  }
  // Local case.  Add effective server address/host to URL so that e.g.
  // 2 reload servers on same port but different network stacks (IPv4 and IPv6)
  // can be uniquely addressed.
  return `http://${address}:${port}/livereload.js?snipver=1`
}
